import React, { useState } from 'react';
import axios from 'axios';
import './CreateAppointment.css';
import { useAppointments } from './AppointmentsContext';

function CreateAppointment() {
    const [patientName, setPatientName] = useState('');
    const [testType, setTestType] = useState('');
    const [radType, setRadType] = useState(''); // New state for Rad type
    const [urgency, setUrgency] = useState(''); // New state for Urgency
    const { addAppointment } = useAppointments();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/appointments', {
                patient_name: patientName,
                test_type: testType,
                rad_type: radType, // Include radType in the appointment data
                urgency: urgency, // Include urgency in the appointment data
            });
            addAppointment(response.data);
        } catch (error) {
            console.error('Error creating appointment:', error);
        }
    };

    return (
        <div className="form-container">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              className="form-field"
              value={patientName}
              onChange={(e) => setPatientName(e.target.value)}
              placeholder="Patient Name"
            />
            <input
              type="text"
              className="form-field"
              value={testType}
              onChange={(e) => setTestType(e.target.value)}
              placeholder="Test Type"
            />
            {/* Rad Type Dropdown */}
            <select
              className="form-field"
              value={radType}
              onChange={(e) => setRadType(e.target.value)}
            >
              <option value="">Select Rad Type</option>
              <option value="Rad 1">Rad 1</option>
              <option value="Rad 2">Rad 2</option>
              <option value="Rad M">Rad M</option>
            </select>
            {/* Urgency Dropdown */}
            <select
              className="form-field"
              value={urgency}
              onChange={(e) => setUrgency(e.target.value)}
            >
              <option value="">Select Urgency</option>
              {[...Array(5).keys()].map((num) => (
                <option key={num} value={num}>{num}</option>
              ))}
            </select>
            <button type="submit" className="form-button">Create Appointment</button>
          </form>
        </div>
    );
}

export default CreateAppointment;
