import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AppointmentsList.css';

function AppointmentsList() {
    const [appointments, setAppointments] = useState([]);

    useEffect(() => {
        fetchAppointments();
    }, []);

    const fetchAppointments = async () => {
        const response = await axios.get('http://127.0.0.1:5000/appointments');
        setAppointments(response.data);
    };

    const deleteAppointment = async (appointmentId) => {
        try {
            await axios.delete(`http://127.0.0.1:5000/appointments/${appointmentId}`);
            // Correctly updates the state to remove the deleted appointment
            setAppointments(currentAppointments => 
                currentAppointments.filter(appointment => appointment.id !== appointmentId)
            );
        } catch (error) {
            console.error('Error deleting appointment:', error);
            // Consider adding user feedback here (e.g., an error message)
        }
    };

    return (
        <div className="list-container">
          <h2>Appointments</h2>
          <ul>
            {appointments.map(appointment => (
              <li key={appointment.id} className="list-item">
                {appointment.patient_name} - {appointment.appointment_date}
                <button onClick={() => deleteAppointment(appointment.id)} className="delete-button">Delete</button>
              </li>
            ))}
          </ul>
        </div>
    );
}

export default AppointmentsList;
