import React, { createContext, useContext, useState } from 'react';
import axios from 'axios';

const AppointmentsContext = createContext();

export function useAppointments() {
  return useContext(AppointmentsContext);
}

export const AppointmentsProvider = ({ children }) => {
  const [appointments, setAppointments] = useState([]);

  const fetchAppointments = async () => {
    // Fetch appointments from the backend and update state
    const response = await axios.get('http://127.0.0.1:5000/appointments');
    setAppointments(response.data);
  };

  const addAppointment = (appointment) => {
    // Add a new appointment to the list in the context (state)
    setAppointments(prevAppointments => [appointment, ...prevAppointments]);
  };

  return (
    <AppointmentsContext.Provider value={{ appointments, fetchAppointments, addAppointment }}>
      {children}
    </AppointmentsContext.Provider>
  );
};
