import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AppointmentsList.css';

import { useAppointments } from './AppointmentsContext'; // Import the hook

function AppointmentsList() {
    const [appointments, setAppointments] = useState([]);

    useEffect(() => {
        fetchAppointments();
    }, []);

    const fetchAppointments = async () => {
        const response = await axios.get('http://127.0.0.1:5000/appointments');
        setAppointments(response.data);
    };

    const deleteAppointment = async (appointmentId) => {
        try {
            await axios.delete(`http://127.0.0.1:5000/appointments/${appointmentId}`);
            setAppointments(appointments.filter(appointment => appointment.id !== appointmentId));
        } catch (error) {
            console.error('Error deleting appointment:', error);
        }
    };

    return (
        <div>
            <h2>Appointments</h2>
            <ul>
                {appointments.map(appointment => (
                    <li key={appointment.id}>
                        Patient Name: {appointment.patient_name}, 
                        Test Type: {appointment.test_type}, 
                        {/* Add delete button */}
                        <button onClick={() => deleteAppointment(appointment.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AppointmentsList;
