import React, { useState } from 'react';
import axios from 'axios';

function CreateAppointment() {
    const [patientName, setPatientName] = useState('');
    const [testType, setTestType] = useState('');
    const [radType, setRadType] = useState(''); // State for Rad Type
    const [urgency, setUrgency] = useState(''); // State for Urgency

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/appointments', {
                patient_name: patientName,
                test_type: testType,
                rad_type: radType, // Include Rad Type in the payload
                urgency: urgency, // Include Urgency in the payload
            });
            console.log('Appointment created:', response.data);
            // Optionally, clear the form fields here
        } catch (error) {
            console.error('Error creating appointment:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <input
                type="text"
                value={patientName}
                onChange={(e) => setPatientName(e.target.value)}
                placeholder="Patient Name"
            />
            <input
                type="text"
                value={testType}
                onChange={(e) => setTestType(e.target.value)}
                placeholder="Test Type"
            />
            {/* Rad Type Dropdown */}
            <select value={radType} onChange={(e) => setRadType(e.target.value)}>
                <option value="">Select Rad Type</option>
                <option value="Rad 1">Rad 1</option>
                <option value="Rad 2">Rad 2</option>
                <option value="Rad M">Rad M</option>
            </select>
            {/* Urgency Dropdown */}
            <select value={urgency} onChange={(e) => setUrgency(e.target.value)}>
                <option value="">Select Urgency</option>
                {[...Array(5).keys()].map((number) => (
                    <option key={number} value={number}>{number}</option>
                ))}
            </select>
            <button type="submit">Create Appointment</button>
        </form>
    );
}

export default CreateAppointment;
